import React from 'react';
import { Button, Form, Input, Card, Icon, Avatar } from 'antd';

const dummy = {
  isLogin: true,
  images: [],
  posts: [
    {
      User: {
        id: 1,
        nickname: 'dongbeen',
      },
      content: 'first content',
      img:
        'https://www.google.co.kr/url?sa=i&url=http%3A%2F%2Frilly.co.kr%2Fproduct%2F%25EB%25B0%2594%25EC%258A%25A4%25EC%25BC%2593%25EB%25B0%25B1-%25EC%258A%25A4%25ED%258A%25B8%25EB%25A7%2581-%25EC%2599%2595%25EA%25B3%25A8-%25EB%259D%25BC%25ED%2583%2584%25EB%25B0%25B1%2F8473%2F&psig=AOvVaw2NdVZ7wsZXiYik_218btfH&ust=1583448083103000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCKDfhPbxgegCFQAAAAAdAAAAABAF',
    },
  ],
};

const Home = () => {
  return (
    <>
      {dummy.isLogin && (
        <Form encType="multipart/form-data">
          <Input.TextArea maxLength={140} placeholder="please input content" />
          <div>
            <Input type="file" multiple hidden />
            <Button>이미지 업로드</Button>
            <Button type="primary" style={{ float: 'right' }} htmlType="submit">
              생성
            </Button>
          </div>
          <div>
            {dummy.images.map((value, index) => {
              return (
                <div key={index} style={{ display: 'inline-block' }}>
                  <img src={value.path} alt={value} />
                  <Button>제거</Button>
                </div>
              );
            })}
          </div>
        </Form>
      )}
      {dummy.posts.map(p => (
        <Card
          key={p.createdAt}
          cover={p.img && <img alt="ex" src={p.img} />}
          actions={[
            <Icon type="retweet" key="retweet" />,
            <Icon type="heart" key="heart" />,
            <Icon type="message" key="message" />,
            <Icon type="ellipsis" key="ellipsis" />,
          ]}>
          <Card.Meta
            avatar={<Avatar>{p.User.nickname[0]}</Avatar>}
            title={p.User.nickname}
            description={p.content}
          />
        </Card>
      ))}
    </>
  );
};

export default Home;
