import React, { useState } from 'react';
import { Form, Input, Checkbox, Button } from 'antd';

const signup = () => {
  const [id, setId] = useState('');
  const [nick, setNick] = useState('');
  const [password, setPassword] = useState('');
  const [passwordCheck, setPasswordCheck] = useState('');
  const [term, setTerm] = useState(false);
  const [passwordError, setPasswordError] = useState(false);
  const [termError, setTermError] = useState(false);
  const [passwordRequired, setPasswordRequired] = useState(false);
  const onSubmit = e => {
    e.preventDefault();
    //webpack에 console.log없애는 설정이 있음
    if (!term) setTermError(true);
    else {
      if (!passwordError && !termError)
        console.log({
          id,
          nick,
          password,
          passwordCheck,
          term,
        });
    }
  };

  const onChangeId = e => {
    setId(e.target.value);
  };

  const onChangeNickname = e => {
    setNick(e.target.value);
  };

  const onChangePassword = e => {
    setPasswordRequired(false);
    setPassword(e.target.value);
  };

  const onChangePasswordCheck = e => {
    if (password === '') {
      setPasswordRequired(true);
    } else {
      setPasswordError(e.target.value !== password);
      setPasswordCheck(e.target.value);
    }
  };

  const onChangeTerm = e => {
    setTermError(!e.target.checked);
    setTerm(e.target.checked);
  };

  return (
    <Form onSubmit={onSubmit}>
      <div>
        <label htmlFor="user-id">아이디</label>
        <br />
        <Input
          name="user-id"
          required={true}
          value={id}
          onChange={onChangeId}
        />
      </div>
      <div>
        <label htmlFor="nickname">닉네임</label>
        <br />
        <Input
          name="nickname"
          required={true}
          value={nick}
          onChange={onChangeNickname}
        />
      </div>
      <div>
        <label htmlFor="user-password">비밀번호</label>
        <br />
        <Input
          type="password"
          name="user-password"
          required={true}
          value={password}
          onChange={onChangePassword}
        />
      </div>
      <div>
        <label htmlFor="user-passwordCheck">비밀번호체크</label>
        <br />
        <Input
          type="password"
          name="user-passwordCheck"
          required={true}
          value={passwordCheck}
          onChange={onChangePasswordCheck}
        />
      </div>{' '}
      {passwordError && (
        <div style={{ color: 'red' }}>비밀번호가 일치하지 않습니다.</div>
      )}
      {passwordRequired && (
        <div style={{ color: 'red' }}>비밀번호를 먼저 입력해주세요.</div>
      )}
      <div>
        <Checkbox name="user-term" value={term} onChange={onChangeTerm}>
          약관동의
        </Checkbox>
        {termError && (
          <div style={{ marginTop: 10, color: 'red' }}>약관에 동의해주세요</div>
        )}
      </div>
      <div>
        <Button type="primary" htmlType="submit">
          가입하기
        </Button>
      </div>
    </Form>
  );
};

export default signup;
